	import SubModule from '@/components/subModule.vue'

	import {		
		getOperator,
		getOperateLog
	} from '@/request/api2'


	export default ({
		components: {
			'sub-menu': SubModule
		},
		data() {
			return {				
				operatorName:[],
				operatorList: [],
				StartTime: '',
				EndTime: '',
				scrollx: '',
				columns:[],
				columnsDefault: [{
					title: '序号',
					dataIndex: 'number',
					scopedSlots: {
						customRender: 'number'
					},
					align: 'center'
				}, {
					title: '内容',
					dataIndex: 'logContent',
					align: 'center'
				}, {
					title: '时间',
					dataIndex: 'logTime',
					align: 'center'
				}, {
					title: '操作人',
					dataIndex: 'person',
					align: 'center'
				}],
				data: [{
					logContent: '1',
					logTime: '1',
					person: '1',
					id: 0
				}, {
					logContent: '1',
					logTime: '1',
					person: '1',
					id: 1
				}, {
					logContent: '1',
					logTime: '1',
					person: '1',
					id: 2
				}],
				loading: false,
				data: [],
				pagination: {
					defaultPageSize: 20,
					pageSize: 20,
					showQuickJumper: true,
					showSizeChanger: true,
					current: 1,
					total: 0
				}
			};
		},
		created() {
			//获取操作人
			getOperator().then(res => {				
				if (res.status === '0') {
					console.log(res)
					this.operatorList = res.result
				} else {
					console.log(res.message)
				}
			})
			let data = {
				PageIndex: this.pagination.current - 1,
				PageSize: this.pagination.pageSize
			}

			this.getFirstPage(data)


		},
		mounted() {},
		methods: {													  
			getFirstPage(data) {
				console.log(data)
				this.loading = true
				// 获取列表表头和首页数据
				this.columns = []
				this.columns = this.columns.concat(this.columnsDefault)
				getOperateLog(data).then(res => {
					console.log(res)
					if (res.status === '0') {						
						this.dataHandle(res.result, this.pagination)
					} else {
						console.log(res.message)
						this.pagination.total = 0
					}
					this.loading = false
				})
			},

			dataHandle(data, pagination) {
				console.log(pagination)
				this.loading = false
				this.data = data.list
				pagination.total = data.total
				this.pagination = pagination
			},

	        getData(data,pagination) {
				console.log(data)
				getOperateLog(data).then(res => {
					if (res.status === '0') {
						this.dataHandle(res.result, pagination)
					} else {
						console.log(res.message)
					}
					this.loading = false
				})
			},

			operatorChange() {

			},

            DateChange(date, dateString) {
				console.log(date, dateString)
				this.StartTime = dateString[0]
				this.EndTime = dateString[1]
			},

            pageChange(pagination, filters, sorter) {
				this.loading = true
				let data = {
			        Operator: this.operatorName.constructor == Array ? '' : this.operatorName,
					StartTime: this.StartTime,
					EndTime: this.EndTime,
					PageIndex: pagination.current - 1,
					PageSize: pagination.pageSize
				}

				this.getData(data, pagination)
			},
			search() {
				this.pagination.current = 1
				this.data = []
				let data = {
				    Operator: this.operatorName.constructor == Array ? '' : this.operatorName,
					StartTime: this.StartTime,
					EndTime: this.EndTime,
					PageIndex: this.pagination.current - 1,
					PageSize: this.pagination.pageSize
				}
				this.getFirstPage(data)
			}

		}
	})
