import SubModule from '@/components/subModule.vue'
import fileDownload from 'js-file-download'

import {
	hourDatas,
	getCompanyList,
	getIndustryType,
	getHoleNames,
	getPollutedLevel
} from '@/request/api'


export default ({
	components: {
		'sub-menu': SubModule
	},
	data() {
		return {
			value: undefined,
			regionData: global.regoins,
			RegionID: '',
			companyList: [],
			CompanyID: [],
			holeList: [],
			CompanyHoleID: [],
			industryTypeList: [],
			IndustryTypeID: [],
			pollutedLevelList: [],
			PollutedLevelID: [],
			StartTime: '',
			EndTime: '',
			scrollx: '',
			fieldNames: {
				children: 'regionChilds',
				label: 'name',
				value: 'id',
			},
			columns: [],
			columnsDefault: [{
					title: '序号',
					dataIndex: 'number',
					scopedSlots: {
						customRender: 'number'
					},
					align: 'center',
					fixed: 'left',
					width: 50
				}, {
					title: '企业名称',
					dataIndex: 'companyName',
					align: 'center',
					fixed: 'left',
					width: 100
				},
				{
					title: '排口名称',
					dataIndex: 'holeName',
					align: 'center',
					fixed: 'left',
					width: 100
				},
				{
					title: '监测时间',
					dataIndex: 'monitorTimeText',
					align: 'center',
					fixed: 'left',
					width: 100
				}, {
					title: '污染物类别',
					dataIndex: 'pollutedType',
					align: 'center',
					fixed: 'left',
					width: 100
				}
			],
			loading: false,
			data: [],
			parameter: {},
			pagination: {
				defaultPageSize: 20,
				pageSize: 20,
				showQuickJumper: true,
				showSizeChanger: true,
				current: 1,
				total: 0
			}
		};
	},
	created() {
		// 获取企业列表
		this.getCompany()
		// 获取行业类别
		getIndustryType().then(res => {
			if (res.status === '0') {
				console.log(res.result)
				this.industryTypeList = res.result
			} else {
				console.log(res.message)
			}
		})
		// 获取污染物等级
		getPollutedLevel().then(res => {
			if (res.status === '0') {
				console.log(res.result)
				this.pollutedLevelList = res.result
			} else {
				console.log(res.message)
			}
		})
		let data = {
			PageIndex: this.pagination.current - 1,
			PageSize: this.pagination.pageSize
		}
		this.getFirstPage(data)
	},
	mounted() {},
	methods: {
		regionChange(value) {
			this.RegionID = value[value.length - 1]
			this.companyList = []
			this.CompanyID = []
		},
		// 获取企业列表方法
		getCompany() {
			getCompanyList({
				regionId: this.RegionID
			}).then(res => {
				if (res.status === '0') {
					this.companyList = res.result
				} else {
					console.log(res.message)
				}
			})
		},
		// 获取排口列表
		getHole(id) {
			getHoleNames({
				regionId: this.RegionID
			}).then(res => {
				if (res.status === '0') {
					console.log(res.result)
					this.holeList = res.result
				} else {
					console.log(res.message)
				}
			})
		},
		companyFocus() {
			this.getCompany()
		},
		companyChange(value) {
			this.CompanyHoleID = []
			this.getHole(value)
		},
		getFirstPage(data) {
			console.log(data)
			this.loading = true
			// 获取列表表头和首页数据
			this.columns = []
			this.columns = this.columns.concat(this.columnsDefault)
			hourDatas(data).then(res => {
				if (res.status === '0') {
					this.scrollx = 350
					const polluteDict = res.result.list[0].polluteDict
					Object.keys(polluteDict).forEach(key => {
						this.columns.push({
							title: key,
							dataIndex: key,
							align: 'center',
							width: 140
						})
						this.scrollx += 140
					});
					this.dataHandle(res.result, this.pagination)
				} else {
					console.log(res.message)
					this.pagination.total = 0
				}
				this.loading = false
			})
		},
		getData(data, pagination) {
			console.log(data)
			hourDatas(data).then(res => {
				if (res.status === '0') {
					this.dataHandle(res.result, pagination)
				} else {
					console.log(res.message)
				}
				this.loading = false
			})
		},
		pageChange(pagination, filters, sorter) {
			this.loading = true
			let data = {
				RegionID: this.RegionID,
				CompanyID: this.CompanyID.constructor == Array ? '' : this.CompanyID,
				CompanyHoleID: this.CompanyHoleID.constructor == Array ? '' : this.CompanyHoleID,
				PollutedLevelID: this.PollutedLevelID.constructor == Array ? '' : this.PollutedLevelID,
				IndustryTypeID: this.IndustryTypeID.constructor == Array ? '' : this.IndustryTypeID,
				StartTime: this.StartTime,
				EndTime: this.EndTime,
				PageIndex: pagination.current - 1,
				PageSize: pagination.pageSize
			}

			this.getData(data, pagination)
		},
		dataHandle(data, pagination) {
			console.log(pagination)
			data.list.forEach(item => {
				item = Object.assign(item, item.polluteDict)
			})
			this.loading = false
			this.data = data.list
			pagination.total = data.total
			this.pagination = pagination
		},
		DateChange(date, dateString) {
			console.log(date, dateString)
			this.StartTime = dateString[0]
			this.EndTime = dateString[1]
		},
		dataSearch() {
			this.pagination.current = 1
			this.data = []
			let data = {
				RegionID: this.RegionID,
				CompanyID: this.CompanyID.constructor == Array ? '' : this.CompanyID,
				CompanyHoleID: this.CompanyHoleID.constructor == Array ? '' : this.CompanyHoleID,
				PollutedLevelID: this.PollutedLevelID.constructor == Array ? '' : this.PollutedLevelID,
				IndustryTypeID: this.IndustryTypeID.constructor == Array ? '' : this.IndustryTypeID,
				StartTime: this.StartTime,
				EndTime: this.EndTime,
				PageIndex: this.pagination.current - 1,
				PageSize: this.pagination.pageSize
			}
			this.getFirstPage(data)
		}
	}
})
