import SubModule from '@/components/subModule.vue'
import fileDownload from 'js-file-download'

import {
	getCompanyList,
	getHoleNames,
	cityElectricStatus
} from '@/request/api'

export default ({
	components: {
		'sub-menu': SubModule
	},
	data() {
		return {
			value: undefined,
			fieldNames: {
				children: 'regionChilds',
				label: 'name',
				value: 'id',
			},
			regionData: global.regoins,
			RegionID: null,
			companyList: [],
			CompanyID: [],
			holeList: [],
			CompanyHoleID: [],
			StartTime: '',
			EndTime: '',
			scrollx: '',
			columns: [{
					title: '序号',
					dataIndex: 'number',
					scopedSlots: {
						customRender: 'number'
					},
					align: 'center',
				}, {
					title: '企业名称',
					dataIndex: 'companyName',
					align: 'center',
				},
				{
					title: '排口名称',
					dataIndex: 'holeName',
					align: 'center',
				}, {
					title: '时间',
					dataIndex: 'cityElectricTimeText',
					align: 'center',
				}, {
					title: '市电状态',
					dataIndex: 'cityElectricStatus',
					align: 'center',
				}, {
					title: '备注',
					dataIndex: 'remarks',
					align: 'center',
				}
			],
			loading: false,
			data: [],
			pagination: {
				defaultPageSize: 20,
				pageSize: 20,
				showQuickJumper: true,
				showSizeChanger: true,
				current: 1,
				total: 0
			}
		};
	},
	created() {
		// 获取企业列表
		this.getCompany()
		let data = {
			PageIndex: this.pagination.current - 1,
			PageSize: this.pagination.pageSize
		}
		// 获取第一页数据
		this.getData(data)
	},
	mounted() {},
	methods: {
		regionChange(value) {
			this.RegionID = value[value.length - 1]
			this.companyList = []
			this.CompanyID = []
		},
		// 获取企业列表方法
		getCompany() {
			getCompanyList({
				regionId: this.RegionID
			}).then(res => {
				if (res.status === '0') {
					this.companyList = res.result
				} else {
					console.log(res.message)
				}
			})
		},
		// 获取排口列表
		getHole(id) {
			getHoleNames({
				regionId: this.RegionID
			}).then(res => {
				if (res.status === '0') {
					console.log(res.result)
					this.holeList = res.result
				} else {
					console.log(res.message)
				}
			})
		},
		companyFocus() {
			this.getCompany()
		},
		companyChange(value) {
			this.CompanyHoleID = []
			this.getHole(value)
		},
		DateChange(date, dateString) {
			console.log(date, dateString)
			this.StartTime = dateString[0]
			this.EndTime = dateString[1]
		},
		getData(data) {
			console.log(data)
			this.loading = true
			cityElectricStatus(data).then(res => {
				if (res.status === '0') {
					console.log(res.result)
					this.data = res.result.list
					const pagination = { ...this.pagination
					}
					pagination.total = res.result.total
					this.pagination = pagination
				} else {
					console.log(res.message)
				}
				this.loading = false
			})
		},
		pageChange(pagination, filters, sorter) {
			const pager = { ...this.pagination
			}
			pager.current = pagination.current
			pager.pageSize = pagination.pageSize
			this.pagination = pager
			let data = {
				RegionID: this.RegionID,
				CompanyID: this.CompanyID.constructor == Array ? '' : this.CompanyID,
				CompanyHoleID: this.CompanyHoleID.constructor == Array ? '' : this.CompanyHoleID,
				StartTime: this.StartTime,
				EndTime: this.EndTime,
				PageIndex: pagination.current - 1,
				PageSize: pagination.pageSize
			}

			this.getData(data)
		},
		search() {
			this.pagination.current = 1
			this.data = []
			let data = {
				RegionID: this.RegionID,
				CompanyID: this.CompanyID.constructor == Array ? '' : this.CompanyID,
				CompanyHoleID: this.CompanyHoleID.constructor == Array ? '' : this.CompanyHoleID,
				StartTime: this.StartTime,
				EndTime: this.EndTime,
				PageIndex: this.pagination.current - 1,
				PageSize: this.pagination.pageSize
			}
			this.getData(data)
		}
	}
})
