	import SubModule from '@/components/subModule.vue'
	import modal from '@/components/chart/well.vue'
	import fileDownload from 'js-file-download'

	import {
		wellNames,
		wellMonitorPoint,
		wellMinuteDatas,
		wellMinuteChart
	} from '@/request/api'

	import {
        exportWellMinuteData
	} from '@/request/api2'


	export default ({
		components: {
			'sub-menu': SubModule,
			'modal': modal
		},
		data() {
			return {
				value: undefined,
				wellList: [],
				WellID: [],
				wellMonitorPointList: [],
				WellMonitorPointID: [],
				StartTime: '',
				EndTime: '',
				scrollx: '',
				scrolly: '',
				fieldNames: {
					children: 'childNameList',
					label: 'regionName',
					value: 'id',
				},
				columns: [],
				columnsDefault: [{
						title: '序号',
						dataIndex: 'number',
						scopedSlots: {
							customRender: 'number'
						},
						align: 'center',
						fixed: 'left',
						width: 50
					}, {
						title: '截污井名称',
						dataIndex: 'wellName',
						align: 'center',
						fixed: 'left',
						width: 100
					},
					{
						title: '监测时间',
						dataIndex: 'monitorTimeText',
						align: 'center',
						fixed: 'left',
						width: 100
					}, {
						title: '污染物类别',
						dataIndex: 'pollutedType',
						align: 'center',
						fixed: 'left',
						width: 100
					}
				],
				loading: false,
				data: [],
				pagination: {
					defaultPageSize: 20,
					pageSize: 20,
					showQuickJumper: true,
					showSizeChanger: true,
					current: 1,
					total: 0
				}
			};
		},
		created() {
			// 获取截污井名称列表
			wellNames().then(res => {
				if (res.status === '0') {
					console.log(res)
					this.wellList = res.result
				} else {
					console.log(res.message)
				}
			})
			let data = {
				PageIndex: this.pagination.current - 1,
				PageSize: this.pagination.pageSize
			}
			// 获取第一页数据
			this.getFirstPage(data)
		},
		mounted() {},
		methods: {
			wellChange(value) {
				console.log(value)
				this.getWellMonitorPoint(value)
			},
			getWellMonitorPoint(id) {
				wellMonitorPoint({
					regionId: id
				}).then(res => {
					if (res.status === '0') {
						console.log(res.result)
						// this.holeList = res.result
					} else {
						console.log(res.message)
					}
				})
			},
			DateChange(date, dateString) {
				console.log(date, dateString)
				this.StartTime = dateString[0]
				this.EndTime = dateString[1]
			},
			getFirstPage(data){
				console.log(data)
				this.loading = true
				// 获取列表表头和首页数据
				this.columns = []
				this.columns = this.columns.concat(this.columnsDefault)
				wellMinuteDatas(data).then(res => {
					console.log(res)
					if (res.status === '0') {
						const polluteDict = res.result.list[0].polluteDict

						const isFixed = Object.keys(polluteDict).length > 10

						this.scrollx = isFixed ? 350 : ''

						this.columns.forEach((item,index)=>{
							item.fixed = isFixed
							if (index && !isFixed) {
								item.width = ''
							}
						})

						Object.keys(polluteDict).forEach(key => {
							this.columns.push({
								title: key,
								dataIndex: key,
								align: 'center',
								width: isFixed ? 140 : ''
							})
							this.scrollx += isFixed ? 140 : ''
						});
						this.dataHandle(res.result, this.pagination)
					} else {
						console.log(res.message)
						this.pagination.total = 0
					}
					this.loading = false
				})
			},
			getData(data, pagination) {
				console.log(data)
				wellMinuteDatas(data).then(res => {
					if (res.status === '0') {
						this.dataHandle(res.result, pagination)
					} else {
						console.log(res.message)
					}
					this.loading = false
				})
			},
			pageChange(pagination, filters, sorter) {
				console.log(pagination.current)
				this.loading = true
				let data = {
					WellID: this.WellID.constructor == Array ? '' : this.WellID,
					WellMonitorPointID: this.WellMonitorPointID.constructor == Array ? '' : this.WellMonitorPointID,
					StartTime: this.StartTime,
					EndTime: this.EndTime,
					PageIndex: pagination.current - 1,
					PageSize: pagination.pageSize
				}

				this.getData(data, pagination)
			},
			dataHandle(data, pagination) {
				console.log(pagination)
				data.list.forEach(item => {
					item = Object.assign(item, item.polluteDict)
				})
				this.loading = false
				this.data = data.list
				pagination.total = data.total
				this.pagination = pagination
			},
			search() {
				this.pagination.current = 1
				this.data = []
				let data = {
					WellID: this.WellID.constructor == Array ? '' : this.WellID,
					WellMonitorPointID: this.WellMonitorPointID.constructor == Array ? '' : this.WellMonitorPointID,
					StartTime: this.StartTime,
					EndTime: this.EndTime,
					PageIndex: this.pagination.current - 1,
					PageSize: this.pagination.pageSize
				}
				this.getFirstPage(data)
			},

			download() {
				let params = {
					WellID: this.WellID.constructor == Array ? '' : this.WellID,
					WellMonitorPointID: this.WellMonitorPointID.constructor == Array ? '' : this.WellMonitorPointID,
					StartTime: this.StartTime,
					EndTime: this.EndTime,
				}
				console.log(params)
				exportWellMinuteData(params).then(res => {
					console.log(res);
					let blob = new Blob([res],{type:'application/vnd.ms-excel'});
					let url = window.URL.createObjectURL(blob); //表示一个指定的file对象或Blob对象
					let a = document.createElement("a");
					a.download = '操作日志报表'; //命名下载名称
					a.style.display="none";
					a.href = url;
					document.body.appendChild(a);
					a.click(); //点击触发下载  
					window.URL.revokeObjectURL(a.href); //下载完成进行释放
					document.body.removeChild(a);
				})
			},
			tableHeight(data) {
				this.scrolly = data - 75
				console.log(this.scrolly)
			},
			chartOpen() {
				this.$refs.modal.visible = true
			},
			chartData(data) {
				console.log(data)
				wellMinuteChart(data).then(res => {
					console.log(res)
					if (res.status === '0') {
						if (res.result.avgList && res.result.avgList.length) {
							this.$refs.modal.avgData = res.result.avgList
							this.$refs.modal.avgunit = res.result.avgUnit
						}
						if ( res.result.rtdList && res.result.rtdList.length) {
							this.$refs.modal.rtdData = res.result.rtdList
							this.$refs.modal.rtdunit = res.result.rtdUnit
						}
						if (res.result.couList && res.result.couList.length) {
							this.$refs.modal.couData = res.result.couList
							this.$refs.modal.couunit = res.result.couUnit
						}
					} else {
						console.log(res.message)
					}
				})
			}
		}
	})
